from setuptools import setupimport shutilimport osAPPNAME = 'Tarakans'APP = ['src/app.py']DATA_FILES = []OPTIONS = {    'argv_emulation': True,    'packages': ['src', 'chardet', 'charset_normalizer'],    'includes': ['customtkinter', 'requests', 'uuid', 'datetime', 'time', 'tk'],    'plist': {        'CFBundleIconFile': 'logo.icns',  # Укажите имя вашего файла иконки    },}setup(    name=APPNAME,    app=APP,    data_files=DATA_FILES,    options={'py2app': OPTIONS},    setup_requires=['setuptools'])# Путь к uninstall.pyuninstall_script_path = 'src/uninstall.py'# Путь к директории инсталлятораinstaller_dir = 'dist/Tarakans.app/Contents/Resources/src'# Создаем директорию, если ее нетos.makedirs(installer_dir, exist_ok=True)# Копируем uninstall.py в директорию инсталлятораshutil.copy(uninstall_script_path, installer_dir)