import subprocessdef uninstall_app(app_name):    try:        # Используйте mdfind для поиска пути к приложению        app_path = subprocess.check_output(['mdfind', f'kMDItemFSName == "{app_name}"']).decode('utf-8').strip()        if app_path:            # Удалите приложение            subprocess.run(['rm', '-rf', app_path])            print(f"Application '{app_name}' removed successfully.")        else:            print(f"Application '{app_name}' not found.")    except Exception as e:        print(f"Error during uninstallation: {e}")if __name__ == "__main__":    app_name_to_uninstall = "Tarakans.app"    uninstall_app(app_name_to_uninstall)