from pymongo import MongoClientclass Database:    def __init__(self, db_name):        self.client = MongoClient('localhost', 27017)  # Подключение к MongoDB        self.db = self.client[db_name]    def register(self):        users_collection = self.db['users']        users_collection.create_index([('mac_addr', 1)], unique=True)  # Создание уникального индекса для MAC_addr    def init_user_data(self, mac_addr, time_limit, start_limit):        users_collection = self.db['users']        result = users_collection.find_one({})        if not result:            user_data = {                "mac_addr": mac_addr,                "time_limit": time_limit,                "start_limit": start_limit            }            users_collection.insert_one(user_data)    def update_start_limit(self, mac_addr, new_start_limit):        users_collection = self.db['users']        query = {"mac_addr": mac_addr}        new_values = {"$set": {"start_limit": new_start_limit}}        users_collection.update_one(query, new_values)    def get_user_data(self, mac_addr):        users_collection = self.db['users']        result = users_collection.find_one({"mac_addr": int(mac_addr)})        if result:            result['_id'] = str(result['_id'])            return result        else:            return None    def clear_table(self):        users_collection = self.db['users']        users_collection.delete_many({})    def close(self):        self.client.close()