from flask import Flask, request, jsonifyfrom db import Databaseapp = Flask(__name__)my_db = Database('app_users')@app.route('/get_user_info', methods=['GET'])def get_user_info():    mac_addr = request.args.get('mac_addr')    if mac_addr:        result = my_db.get_user_data(mac_addr)        if result:            return jsonify(result)        else:            return "User not found"    else:        return "No 'mac_addr' parameter provided"@app.route('/add', methods=['POST'])def add():    data = request.get_json()    my_db.init_user_data(data['mac_addr'], data['time_limit'], data['start_limit'])    return 'Good add'@app.route('/update', methods=['POST'])def update():    data = request.get_json()    my_db.update_start_limit(data['mac_addr'], data['start_limit'])    return 'Good update'if __name__ == '__main__':    app.run(host='127.0.0.1', port=5000)